scheduler:
var newID=-1;
var minStartTime = 10000;
for (var i = 0; i < processControlBlock.length; i++)
{
    if (processControlBlock[i].state ==="START" || 
           processControlBlock[i].state ==="READY")
    {
        if(parseInt(processControlBlock[i].lastCPUTime) 
              <minStartTime)
        {
            minStartTime = 
                parseInt(processControlBlock[i].lastCPUTime);
            newID = processControlBlock[i].id;
        }
    }
}
return newID;
